ext().css(cssMedium);			$('#carousel').carouFredSel({		width: '100%',		height: 250,		items: 5,		scroll: {			items: 1,			duration: aniConf.duration,			onBefore: function( data ) {																//	0 [ 1 ] 2  3  4				data.items.old.eq(1).animate(cssSmall, aniConf);								//	0  1 [ 2 ] 3  4				data.items.old.eq(2).animate(cssMedium, aniConf);								// 0  1  2  [ 3 ] 4				data.items.old.eq(3).animate(cssLarge, aniConf);								//	0  1  2  3 [ 4 ]				data.items.old.eq(4).animate(cssMedium, aniConf);			}		}	}); };</script><script src="js/main.js"></script></html>